fn main() {
    let final_floor = path.chars().fold(0, |floor, step| {
        if step == '(' {
            return floor + 1;
        } else {
            return floor - 1;
        }
    });
    let first_basement = path.chars().fold([0, 0, 0], |floor, step| {
        let mut tmp = floor;
        tmp = [tmp[0], tmp[1], tmp[2] + 1];
        if step == '(' {
            tmp = [floor[0] + 1, tmp[1], tmp[2]]
        } else {
            tmp = [floor[0] - 1, tmp[1], tmp[2]];
        }
        println!("{:?}", tmp);
        if tmp[0] < 0 && tmp[1] == 0 {
            tmp = [tmp[0], tmp[2], tmp[2]];
        }
        return tmp;
    });
    println!("Final Floor: {}", final_floor);
        println!("First Basement Step: {}", first_basement[1]);
}
